import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useApi } from '../hooks/useApi';
import '../styles/appointment-detail.css';

const AppointmentDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [appointment, setAppointment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [actionLoading, setActionLoading] = useState(false);
  const { user } = useAuth();
  const { get, put } = useApi();

  useEffect(() => {
    fetchAppointmentDetail();
  }, [id]);

  const fetchAppointmentDetail = async () => {
    try {
      setLoading(true);
      const response = await get(`/api/appointments/${id}`);
      setAppointment(response.data);
    } catch (err) {
      setError('Failed to fetch appointment details');
      console.error('Error fetching appointment:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleAcceptAppointment = async () => {
    try {
      setActionLoading(true);
      await put(`/api/appointments/${id}/accept`);
      setAppointment(prev => ({ ...prev, status: 'confirmed' }));
    } catch (err) {
      setError('Failed to accept appointment');
      console.error('Error accepting appointment:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const handleCancelAppointment = async () => {
    try {
      setActionLoading(true);
      await put(`/api/appointments/${id}/cancel`);
      setAppointment(prev => ({ ...prev, status: 'cancelled' }));
    } catch (err) {
      setError('Failed to cancel appointment');
      console.error('Error cancelling appointment:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="appointment-detail-page">
        <div className="loading">Loading appointment details...</div>
      </div>
    );
  }

  if (!appointment) {
    return (
      <div className="appointment-detail-page">
        <div className="error-message">Appointment not found</div>
      </div>
    );
  }

  return (
    <div className="appointment-detail-page">
      <div className="appointment-detail-header">
        <h1>Appointments</h1>
        <p className="page-subtitle">Garage Overview and Activities</p>
      </div>

      <div className="appointment-detail-content">
        <div className="section-header">
          <h2>Appointment Summary</h2>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <div className="appointment-summary-card">
          <div className="summary-header">
            {appointment.priority === 'high' && (
              <span className="priority-badge high-urgency">High Urgency</span>
            )}
          </div>

          <div className="summary-details">
            <div className="detail-row">
              <div className="detail-icon">👤</div>
              <div className="detail-content">
                <span className="detail-value">{appointment.customerName}</span>
              </div>
            </div>

            <div className="detail-row">
              <div className="detail-icon">🚗</div>
              <div className="detail-content">
                <span className="detail-value">
                  {appointment.vehicleMake} {appointment.vehicleModel} {appointment.vehicleYear}
                </span>
              </div>
            </div>

            <div className="detail-row">
              <div className="detail-icon">🔧</div>
              <div className="detail-content">
                <span className="detail-label">Service Needed:</span>
                <span className="detail-value">{appointment.serviceType}</span>
              </div>
            </div>

            <div className="detail-row">
              <div className="detail-icon">📅</div>
              <div className="detail-content">
                <span className="detail-value">{formatDate(appointment.appointmentDate)}</span>
              </div>
            </div>

            <div className="detail-row">
              <div className="detail-icon">🕐</div>
              <div className="detail-content">
                <span className="detail-value">Requested today</span>
              </div>
            </div>
          </div>

          {appointment.status === 'pending' && (
            <div className="appointment-actions">
              <button 
                className="accept-btn"
                onClick={handleAcceptAppointment}
                disabled={actionLoading}
              >
                {actionLoading ? 'Processing...' : 'Accept Appointment'}
              </button>
              <button 
                className="cancel-btn"
                onClick={handleCancelAppointment}
                disabled={actionLoading}
              >
                Cancel Appointment
              </button>
            </div>
          )}

          {appointment.status === 'confirmed' && (
            <div className="appointment-status">
              <span className="status-confirmed">✓ Appointment Confirmed</span>
            </div>
          )}

          {appointment.status === 'cancelled' && (
            <div className="appointment-status">
              <span className="status-cancelled">✗ Appointment Cancelled</span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AppointmentDetailPage;
