import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useRegistration } from '../context/RegistrationContext';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './VerificationPage.css';

const VerificationPage = () => {
    const [verificationCode, setVerificationCode] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [sentCode, setSentCode] = useState('');
    const { registrationData, resetRegistration } = useRegistration();
    const { register } = useAuth();
    const navigate = useNavigate();

    // Send verification code when component mounts
    useEffect(() => {
        const sendCode = async () => {
            try {
                const response = await axios.post('http://localhost:5000/api/auth/send-verification', {
                    email: registrationData.email
                });
                setSentCode(response.data.verificationCode); // For demo purposes
            } catch (err) {
                setError('Failed to send verification code');
            }
        };

        if (registrationData.email) {
            sendCode();
        }
    }, [registrationData.email]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        if (!verificationCode || verificationCode.length !== 6) {
            setError('Please enter a valid 6-digit verification code');
            setLoading(false);
            return;
        }

        try {
            // Create the user account with all the registration data
            await register({
                firstName: registrationData.firstName,
                lastName: registrationData.lastName,
                email: registrationData.email,
                password: registrationData.password,
                phone: registrationData.phone,
                address: registrationData.address,
                street: registrationData.street,
                state: registrationData.state,
                location: registrationData.location,
                verificationCode: verificationCode
            });

            // Clear registration data
            resetRegistration();
            
            // Navigate to dashboard
            navigate('/dashboard');
        } catch (err) {
            setError(err.response?.data?.message || 'Verification failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleResendCode = async () => {
        try {
            const response = await axios.post('http://localhost:5000/api/auth/send-verification', {
                email: registrationData.email
            });
            setSentCode(response.data.verificationCode);
            alert(`New verification code: ${response.data.verificationCode}`);
        } catch (err) {
            setError('Failed to resend code');
        }
    };

    return (
        <div className="verification-page">
            {/* Simple Header */}
            <div className="verification-header">
                <div className="header-logo">
                    <img 
                        src={`${process.env.PUBLIC_URL}/autoSaaz-logo.png`}
                        alt="AutoSaaz" 
                        className="logo-image"
                    />
                    <div className="header-text">
                        <span className="company-name">AutoSaaz</span>
                        <span className="tagline">One Stop Auto Shop</span>
                    </div>
                </div>
            </div>

            {/* Centered Content */}
            <div className="verification-content">
                <div className="verification-card">
                    <h1>Enter Verification Code</h1>
                    <p>We've sent a 6-digit code to your email or phone number.</p>
                    
                    {sentCode && (
                        <div className="demo-code">
                            <strong>Demo Code: {sentCode}</strong>
                        </div>
                    )}

                    {error && <div className="error-message">{error}</div>}

                    <form onSubmit={handleSubmit} className="verification-form">
                        <div className="code-inputs">
                            <input
                                type="text"
                                value={verificationCode}
                                onChange={(e) => setVerificationCode(e.target.value)}
                                placeholder="Enter 6-digit code"
                                maxLength="6"
                                className="code-input"
                                required
                            />
                        </div>

                        <button 
                            type="submit" 
                            className="verify-button"
                            disabled={loading}
                        >
                            {loading ? 'Verifying...' : 'Verify Code'}
                        </button>
                    </form>

                    <div className="resend-section">
                        <span>Didn't get the code? </span>
                        <button 
                            type="button" 
                            className="resend-link"
                            onClick={handleResendCode}
                        >
                            Resend Code
                        </button>
                    </div>

                    <div className="back-section">
                        <button 
                            type="button" 
                            className="back-button"
                            onClick={() => navigate('/register-step-2')}
                        >
                            ← Back
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default VerificationPage;
