import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import DEV_CONFIG from '../../config/dev';

// UpperNavbar: persistent top bar with dynamic page title/description and quick actions
const UpperNavbar = () => {
  const location = useLocation();
  const { user } = useAuth();
  const path = location.pathname;
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'light');

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => setTheme(t => (t === 'light' ? 'dark' : 'light'));

  // Derive title/subtitle
  let title = 'Dashboard';
  let subtitle = 'Garage Overview and Activities';

  if (path.startsWith('/appointments')) {
    title = 'Appointments';
    subtitle = /\/appointments\/[A-Za-z0-9_-]+$/.test(path) ? 'Appointment Detail' : 'Garage Overview and Activities';
  } else if (path.startsWith('/inspections')) {
    if (path.includes('/completed')) { title = 'Completed Inspections'; subtitle = 'Garage Overview and Activities'; }
    else if (path.includes('/pending')) { title = 'Pending Inspections'; subtitle = 'Garage Overview and Activities'; }
    else { title = 'Inspection Detail'; subtitle = 'Garage Overview and Activities'; }
  } else if (path.startsWith('/resolution-center')) {
    title = 'Resolution Center';
    if (path.includes('/resolved')) subtitle = 'Resolved Cases';
    else if (path.includes('/disputes/')) subtitle = 'Dispute Conversation';
    else subtitle = 'Issue Resolution & Support';
  } else if (path.startsWith('/chats')) {
    title = 'Chats'; subtitle = 'Customer & Internal Conversations';
  } else if (path.startsWith('/settings')) {
    title = 'Settings'; subtitle = 'Manage Account & Preferences';
  }

  const businessName = user?.businessName || 'AAA Auto Garage';
  const avatarLetter = (user?.first_name || user?.name || businessName || 'U').charAt(0).toUpperCase();

  return (
    <header className="header upper-navbar" role="banner">
      <div className="header-content">
        <div className="page-info">
          <h1>{title}</h1>
          <p className="page-subtitle">{subtitle}</p>
        </div>
        <div className="header-actions">
          {/* Notification */}
          <button className="notification-btn" aria-label="Notifications">
            <span className="bell-icon" role="img" aria-hidden="true">ðŸ””</span>
          </button>
          {/* Night Mode Toggle */}
          <button
            className="settings-btn theme-toggle-btn"
            aria-label={theme === 'light' ? 'Switch to dark mode' : 'Switch to light mode'}
            onClick={toggleTheme}
            title="Toggle Theme"
          >
            {theme === 'light' ? 'ðŸŒ™' : 'ðŸŒž'}
          </button>
          {/* (Optional) Dev auth toggle retained but tiny */}
          {process.env.NODE_ENV === 'development' && (
            <button
              onClick={() => { DEV_CONFIG.ENABLE_AUTH = !DEV_CONFIG.ENABLE_AUTH; window.location.reload(); }}
              className="dev-toggle"
              style={{
                background: DEV_CONFIG.ENABLE_AUTH ? '#ff6b35' : '#28a745',
                color: 'white', border: 'none', padding: '4px 6px', borderRadius: '4px', fontSize: '10px', cursor: 'pointer'
              }}
              title="Toggle Authentication (Dev Only)"
            >
              Auth: {DEV_CONFIG.ENABLE_AUTH ? 'ON' : 'OFF'}
            </button>
          )}
          {/* Business / User info */}
          <div className="business-info">
            <span className="business-name">{businessName}</span>
            <span className="business-type">Mechanics</span>
            <div className="business-logo" aria-label="User Avatar">
              <span className="garage-icon" aria-hidden="true">{avatarLetter}</span>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default UpperNavbar;
