import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import DEV_CONFIG from '../../config/dev';

const Header = () => {
    const { user } = useAuth();
    const location = useLocation();

    // Don't show header on auth pages
    if (location.pathname === '/login' || location.pathname === '/register') {
        return null;
    }

    const toggleAuth = () => {
        DEV_CONFIG.ENABLE_AUTH = !DEV_CONFIG.ENABLE_AUTH;
        window.location.reload(); // Reload to apply changes
    };

    // Derive page title + subtitle
    let title = 'Dashboard';
    let subtitle = 'Garage Overview and Activities';
    const p = location.pathname;
    if (p.startsWith('/appointments')) {
        title = 'Appointments';
    } else if (p.startsWith('/inspections')) {
        if (p.includes('/completed')) title = 'Completed Inspections';
        else title = 'Pending Inspections';
    } else if (p.startsWith('/resolution-center')) {
        if (p.includes('/resolved')) title = 'Resolved Disputes'; else if (p.includes('/disputes/')) title = 'Dispute Conversation'; else title = 'Resolution Center';
        subtitle = 'Issue Resolution and Support';
    } else if (p.startsWith('/chats')) {
        title = 'Chats';
        subtitle = 'Conversations';
    } else if (p.startsWith('/settings')) {
        title = 'Settings';
        subtitle = 'Manage Preferences';
    }

    return (
        <header className="header">
            <div className="header-content">
                <div className="page-info">
                    <h1>{title}</h1>
                    <p className="page-subtitle">{subtitle}</p>
                </div>

                <div className="header-actions">
                    {/* Development Auth Toggle */}
                    {process.env.NODE_ENV === 'development' && (
                        <button 
                            onClick={toggleAuth}
                            className="dev-toggle"
                            style={{
                                background: DEV_CONFIG.ENABLE_AUTH ? '#ff6b35' : '#28a745',
                                color: 'white',
                                border: 'none',
                                padding: '4px 8px',
                                borderRadius: '4px',
                                fontSize: '10px',
                                cursor: 'pointer',
                                marginRight: '10px'
                            }}
                            title="Toggle Authentication (Dev Only)"
                        >
                            Auth: {DEV_CONFIG.ENABLE_AUTH ? 'ON' : 'OFF'}
                        </button>
                    )}

                    <button className="notification-btn">
                        <i className="bell-icon">üîî</i>
                    </button>
                    <button className="settings-btn">
                        <i className="settings-icon">‚öôÔ∏è</i>
                    </button>
                    <div className="business-info">
                        <span className="business-name">
                            {user?.businessName || 'AAA Auto Garage'}
                        </span>
                        <span className="business-type">Mechanics</span>
                        <div className="business-logo">
                            <i className="garage-icon">üîß</i>
                        </div>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
