import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import { useApi } from '../hooks/useApi';
import Sidebar from '../components/Dashboard/Sidebar';
import '../components/Dashboard/Dashboard.css';
import '../styles/appointments.css';

const AppointmentsPage = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();
  const { get } = useApi();

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      setLoading(true);
      const response = await get('/api/appointments');
      setAppointments(response.data || []);
    } catch (err) {
      setError('Failed to fetch appointments');
      console.error('Error fetching appointments:', err);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'long',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  if (loading) {
    return (
      <div className="dashboard">
        <Sidebar />
        <div className="dashboard-main">
          <div className="dashboard-header">
            <div className="header-left">
              <h1 className="dashboard-title">Appointments</h1>
              <p className="dashboard-subtitle">Manage your garage appointments</p>
            </div>
          </div>
          <div className="dashboard-content">
            <div className="loading">Loading appointments...</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <Sidebar />
      <div className="dashboard-main">
        <div className="dashboard-header">
          <div className="header-left">
            <h1 className="dashboard-title">Appointments</h1>
            <p className="dashboard-subtitle">Manage your garage appointments</p>
          </div>
        </div>
        <div className="dashboard-content">
          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {appointments.length === 0 && !loading ? (
            <div className="no-appointments">
              <p>No upcoming appointments scheduled.</p>
            </div>
          ) : (
            <div className="appointments-list">
              {appointments.map((appointment) => (
                <div key={appointment.id} className="appointment-card">
                  <div className="appointment-date-time">
                    <div className="date-section">
                      <i className="calendar-icon">📅</i>
                      <span className="date">{formatDate(appointment.appointmentDate)}</span>
                    </div>
                    <div className="time-section">
                      <i className="clock-icon">🕐</i>
                      <span className="time">{formatTime(appointment.appointmentDate)}</span>
                    </div>
                  </div>

                  <div className="appointment-details">
                    <div className="customer-info">
                      <h3 className="customer-name">{appointment.customerName}</h3>
                      <div className="vehicle-info">
                        <i className="car-icon">🚗</i>
                        <span>{appointment.vehicleMake} {appointment.vehicleModel} ({appointment.vehicleYear})</span>
                      </div>
                    </div>

                    <div className="appointment-actions">
                      <div className="appointment-status">
                        <span className={`status-badge ${appointment.status.toLowerCase()}`}>
                          {appointment.status === 'confirmed' ? '● Confirmed' : '○ Pending'}
                        </span>
                        {appointment.priority === 'high' && (
                          <span className="priority-badge">Drop off</span>
                        )}
                      </div>
                      <Link 
                        to={`/appointments/${appointment.id}`} 
                        className="view-details-btn"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AppointmentsPage;
