import React from 'react';
import { useLocation } from 'react-router-dom';
import Header from './Header';
import Sidebar from '../Dashboard/Sidebar';
import './Layout.css';
import './Header.css';

const Layout = ({ children }) => {
    const location = useLocation();

    // Show different layout for auth pages, home page, and dashboard pages
    const isAuthPage = location.pathname === '/login' || 
                      location.pathname === '/register' || 
                      location.pathname === '/register-step-2' || 
                      location.pathname === '/verify-account';
    const isHomePage = location.pathname === '/';
    const isDashboardPage = location.pathname === '/dashboard' ||
                           location.pathname === '/appointments' ||
                           location.pathname.startsWith('/appointments/') ||
                           location.pathname === '/inspections' ||
                           location.pathname === '/chats' ||
                           location.pathname === '/resolution-center' ||
                           location.pathname === '/settings';

    if (isAuthPage || isHomePage || isDashboardPage) {
        return (
            <div className="auth-layout">
                {children}
            </div>
        );
    }

    return (
        <div className="dashboard-layout">
            <Sidebar />
            <div className="main-wrapper">
                <Header />
                <main className="main-content">
                    {children}
                </main>
            </div>
        </div>
    );
};

export default Layout;
