import React from 'react';
import { useEffect, useState } from 'react';
import { getBookings } from '../../services/api';
import './BookingSummary.css';

const BookingSummary = () => {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const data = await getBookings();
                setBookings(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchBookings();
    }, []);

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toISOString().split('T')[0]; // Format as YYYY-MM-DD
    };

    const getStatusClass = (status) => {
        switch (status?.toLowerCase()) {
            case 'completed':
                return 'status-completed';
            case 'pending':
                return 'status-in-progress';
            case 'confirmed':
                return 'status-in-progress';
            default:
                return 'status-in-progress';
        }
    };

    const getStatusText = (status) => {
        switch (status?.toLowerCase()) {
            case 'completed':
                return 'Completed';
            case 'pending':
                return 'In Progress';
            case 'confirmed':
                return 'In Progress';
            default:
                return 'In Progress';
        }
    };

    if (loading) {
        return (
            <div className="booking-summary-loading">
                <div className="loading-spinner">Loading bookings...</div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="booking-summary-error">
                <p>Error loading bookings: {error}</p>
            </div>
        );
    }

    return (
        <div className="booking-summary">
            <div className="section-header">
                <h2 className="section-title">Booking Summary</h2>
            </div>

            {bookings.length === 0 ? (
                <div className="no-bookings">
                    <p>No bookings found.</p>
                </div>
            ) : (
                <div className="booking-table-container">
                    <table className="booking-table">
                        <thead>
                            <tr>
                                <th>Booking ID</th>
                                <th>Customer</th>
                                <th>Service</th>
                                <th>Date</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {bookings.map((booking, index) => (
                                <tr key={booking.id}>
                                    <td className="booking-id">#{1001 + index}</td>
                                    <td className="customer-name">{booking.customer_name}</td>
                                    <td className="service-type">{booking.service_type}</td>
                                    <td className="booking-date">{formatDate(booking.appointment_date)}</td>
                                    <td className="booking-status">
                                        <span className={`status-badge ${getStatusClass(booking.status)}`}>
                                            {getStatusText(booking.status)}
                                        </span>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default BookingSummary;
