import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getInspections } from '../services/inspections.service';
import './InspectionsPage.css';

const InspectionsPage = () => {
    const [activeTab, setActiveTab] = useState('pending');
    const [inspections, setInspections] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchInspections = async () => {
            try {
                setLoading(true);
                const data = await getInspections();
                setInspections(data);
            } catch (err) {
                setError('Failed to load inspections');
                console.error('Error fetching inspections:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchInspections();
    }, []);

    const pendingInspections = inspections.filter(inspection => inspection.status === 'pending');
    const completedInspections = inspections.filter(inspection => inspection.status === 'completed');

    const handleViewDetails = (inspectionId) => {
        navigate(`/inspections/${inspectionId}`);
    };

    const InspectionCard = ({ inspection }) => (
        <div className="inspections-page-card">
            <div className="inspections-page-card-header">
                <h3 className="inspections-page-card-title">
                    {inspection.customer} - {inspection.vehicle}
                </h3>
                <p className="inspections-page-card-subtitle">
                    Inspection due today at {inspection.time} | Assigned: Technician {inspection.assignedTechnician}
                </p>
            </div>
            <div className="inspections-page-card-content">
                <div className="inspections-page-task-list">
                    {inspection.tasks.map((task, index) => (
                        <div key={index} className="inspections-page-task-item">
                            <span className="inspections-page-task-bullet">‚óè</span>
                            <span className="inspections-page-task-text">{task}</span>
                        </div>
                    ))}
                </div>
                <button 
                    className="inspections-page-view-details-btn"
                    onClick={() => handleViewDetails(inspection.id)}
                >
                    View Details
                </button>
            </div>
        </div>
    );

    if (loading) {
        return (
            <div className="inspections-page-container">
                <div className="inspections-page-header">
                    <h1 className="inspections-page-title">Inspection</h1>
                    <p className="inspections-page-subtitle">Garage Overview and Activities</p>
                </div>
                <div className="inspections-page-loading">Loading inspections...</div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="inspections-page-container">
                <div className="inspections-page-header">
                    <h1 className="inspections-page-title">Inspection</h1>
                    <p className="inspections-page-subtitle">Garage Overview and Activities</p>
                </div>
                <div className="inspections-page-error">{error}</div>
            </div>
        );
    }

    return (
        <div className="inspections-page-container">
            <div className="inspections-page-header">
                <h1 className="inspections-page-title">Inspection</h1>
                <p className="inspections-page-subtitle">Garage Overview and Activities</p>
            </div>

            <div className="inspections-page-tabs">
                <button 
                    className={`inspections-page-tab ${activeTab === 'pending' ? 'inspections-page-tab-active' : ''}`}
                    onClick={() => setActiveTab('pending')}
                >
                    Pending
                </button>
                <button 
                    className={`inspections-page-tab ${activeTab === 'completed' ? 'inspections-page-tab-active' : ''}`}
                    onClick={() => setActiveTab('completed')}
                >
                    Completed
                </button>
            </div>

            <div className="inspections-page-content">
                {activeTab === 'pending' && (
                    <div className="inspections-page-section">
                        <h2 className="inspections-page-section-title">Pending</h2>
                        <div className="inspections-page-cards">
                            {pendingInspections.length > 0 ? (
                                pendingInspections.map(inspection => (
                                    <InspectionCard key={inspection.id} inspection={inspection} />
                                ))
                            ) : (
                                <div className="inspections-page-empty">No pending inspections</div>
                            )}
                        </div>
                    </div>
                )}

                {activeTab === 'completed' && (
                    <div className="inspections-page-section">
                        <h2 className="inspections-page-section-title">Completed Reports</h2>
                        <div className="inspections-page-cards">
                            {completedInspections.length > 0 ? (
                                completedInspections.map(inspection => (
                                    <InspectionCard key={inspection.id} inspection={inspection} />
                                ))
                            ) : (
                                <div className="inspections-page-empty">No completed inspections</div>
                            )}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default InspectionsPage;
